SELECT session_id, car_number, Date as telemetry_datetime, RPM as rpm, Speed as speed, nGear as gear_number, Throttle as throttle_percentage, Brake as is_braking, CAST(DRS as Nullable(Bool)) as is_drs_enabled, Source as telemetry_source, Time as time_from_lap_start, SessionTime as time_from_session_start, Status as on_track_status, X as x_coordinate, Y as y_coordinate, Z as z_coordinate FROM file('fastf1/telemetry.parquet', 'Parquet')
